    'Ancona' : [''], 'Bari' : [''], 'Bergamo' : [''], 'Bologna' : [''], 'Brindisi' : [''], 'Cagliari' : [''], 'Milan' : [''], 'Naples' : [''],'Pisa' : [''], 'Palermo' : [''], 'Perugia' : [''], 'Rome' : [''], 'Turin' : [''], 'Venice' : [''], 'Verona' : [''],
