import React, { useState } from 'react';
import "../style/Home.css";

function showReturnDate() {
    var x = document.getElementById("returnDate");
    if (x.style.display === "none") {
      x.style.display = "block";
    } else {
      x.style.display = "none";
    }
  }

function sendSearchResults(event, searchParams) {
    event.preventDefault(); // prevent the default form submission behavior

    fetch('http://127.0.0.1:5000/search_inputs', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json;charset=UTF-8'
        },
        body: JSON.stringify(searchParams)
    })
    window.location.href = "/results";
}

let visible = [];

function Home() {

    const [departureLocation, setDepartureLocation] = useState('');
    const [destination, setDestination] = useState('');
    const [departDate, setDepartDate] = useState('');
    const [returnDate, setReturnDate] = useState('');
    const [numLayovers, setNumLayovers] = useState('Non-stop');
    const [time, setTime] = useState(288);
    const [cost, setCost] = useState(100);
    const [sortType, setSortType] = useState('cost');
    const [searched, setSearched] = useState(false);

    function Results() {
        return visible.map(result => 
                <tr>
                <td>{result[0]}</td>
                <td>{result[1]}</td>
                <td>{result[2]}</td>
                <td>{result[3]}</td>
                <td>{parseInt(result[4]/60)} hr {result[4] % 60} min</td>
                <td>€{result[5]}</td>
                <td>{result[6]}</td>
                </tr>
            );
    }

    function update(){
        visible = [];
        for (let i = 0; i < master.length; i++){
            if (master[i][4] <= time*5 && master[i][5] <= cost*10){
                visible.push(master[i]);
            }
        }
            sortingChange = false;
            if (sortType === 'cost'){
                visible.sort(function compareFn(a, b) { 
                    if (a[5] < b[5]){
                        return -1
                    }
                    else if (a[5] > b[5]){
                        return 1
                    }
                    else {
                        return 0
                    }
                });
            }
            else if (sortType === 'time'){
                visible.sort(function compareFn(a, b) { 
                    if (a[4] < b[4]){
                        return -1
                    }
                    else if (a[4] > b[4]){
                        return 1
                    }
                    else {
                        return 0
                    }
                });
            }
            else{
                visible.sort(function compareFn(a, b) { 
                    if (a[1] < b[1]){
                        return -1
                    }
                    else if (a[1] > b[1]){
                        return 1
                    }
                    else {
                        if (a[5] < b[5]){
                            return -1
                        }
                        else if (a[5] > b[5]){
                            return 1
                        }
                        else {
                            return 0
                        }
                    }
                });
            }
    }

    function Page(){
        if (!searched){
            return (
                <div>
                <script src="https://unpkg.com/vue@3"></script>
                <script src="https://cdn.jsdelivr.net/npm/vue/dist/vue.js"></script>
                <div class="header" className="App-header">
                    <h1>dinoSOAR</h1>
                </div>
                <div className="App-body">
                    <h2>Search for Flights!</h2>

                    <p>Chose flight option</p>
                    <div class="btn-flight-opt">
                        <button>One Way</button>
                        <button>Round Trip</button>
                    </div>

                    <form onSubmit={(event) => sendSearchResults(event, searchParams)}>
                        <label htmlFor="from">From:</label>
                        <input
                            type="text"
                            id="from"
                            name="from"
                            value={departureLocation}
                            onChange={(event) => setDepartureLocation(event.target.value)}
                        />

                        <label htmlFor="to">To:</label>
                        <input
                            type="text"
                            id="to"
                            name="to"
                            value={destination}
                            onChange={(event) => setDestination(event.target.value)}
                        /><br />

                        <label htmlFor="departDate">Departure Date:</label>
                        <input
                            type="date"
                            id="departDate"
                            name="departDate"
                            value={departDate}
                            onChange={(event) => setDepartDate(event.target.value)}
                        />

                        <label htmlFor="returnDate">Return Date:</label>
                        <input
                            type="date"
                            id="returnDate"
                            name="returnDate"
                            value={returnDate}
                            onChange={(event) => setReturnDate(event.target.value)}
                        /><br /><br />

                        <input
                            type="radio"
                            id="noLayover"
                            name="numLayovers"
                            value="Non-stop"
                            checked={numLayovers === 'Non-stop'}
                            onChange={(event) => setNumLayovers(event.target.value)}
                        />
                        <label htmlFor="noLayover">Non-stop</label>


                        <input
                            type="radio"
                            id="oneLayover"
                            name="numLayovers"
                            value="1 Stop"
                            checked={numLayovers === '1 Stop'}
                            onChange={(event) => setNumLayovers(event.target.value)}
                        />
                        <label htmlFor="oneLayover">1 Stop</label>


                        <input
                            type="radio"
                            id="twoLayover"
                            name="numLayovers"
                            value="2 Stop"
                            checked={numLayovers === '2 Stop'}
                            onChange={(event) => setNumLayovers(event.target.value)}
                        />
                        <label htmlFor="twoLayover">2 Stops</label><br></br><br></br>


                        <input type="submit" id="search" value="Search" ></input>
                    </form>

                </div>
            </div>
            );
        }
        else {
            return (
                <div>
                <div class="header" className="App-header">
                    <h1>dinoSOAR</h1>
                </div>

                <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/css/bootstrap.min.css"/>
                <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.6.4/jquery.min.js"></script>
                <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/js/bootstrap.min.js"></script>
                <script src="https://unpkg.com/vue@3/dist/vue.global.js"></script>

                <div className="App-body">
                    <div class="container">
                        <h1>Filters:</h1>
                        <div class="row">
                            <div class="col-sm-6">
                                <div className="Slider">
                                    <h2>Cost Limits</h2>
                                    <p>Move the slider to choose how much you are willing to pay foryour trip.</p>
                                    <input id="costRange"
                                        max={100}
                                        type="range"
                                        value={cost}
                                        onChange={(e) => setCost(e.target.valueAsNumber)}
                                    />
                                    <p>Maximum cost: €{cost*10}</p>
                                </div>
                            </div>
                            <div class="col-sm-6">
                                <div className="Slider">
                                    <h2>Time Limits</h2>
                                    <p>Move the slider to choose how long you are willing your trip to take.</p>
                                    <input id="timeRange"
                                        max={288}
                                        type="range"
                                        value={time}
                                        onChange={(e) => setTime(e.target.valueAsNumber)}
                                    />
                                    <p>Maximum time: {parseInt(time*5/60)} hr {time*5 % 60} min</p>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div class="container">
                        <h2>Sort results by...</h2>
                        <div class="col-sm-1">
                            <button onClick={() => setSortType('cost')}>
                                Cost
                            </button>
                        </div>
                        <div class="col-sm-1">
                            <button onClick={() => setSortType('time')}>
                                Time
                            </button>
                        </div>
                        <div class="col-sm-1">
                            <button onClick={() => setSortType('airline')}>
                                Airline
                            </button>
                        </div>
                    </div>
                    <div class="container">
                        <h1>Flight Results: currently showing {visible.length} flight options sorted by {sortType}.</h1>  
                        <table class="table table-bordered">
                            <thead>
                                <tr>
                                    <th>Flight number</th>
                                    <th>Airline</th>
                                    <th>Departure Time</th>
                                    <th>Arrival Time</th>
                                    <th>Total Time</th>
                                    <th>Total Cost</th>
                                    <th># Layovers</th>
                                </tr>
                            </thead>
                            <tbody>
                                <Results 
                                number="A4546" 
                                airline="Lufthansa"
                                departure="10:10"
                                arrival="12:35"
                                time="2 hr 25 min"
                                cost="€98"
                                layovers="0"
                                />
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
            );
        }
    }

    const searchParams = {
        from: departureLocation,
        to: destination,
        departDate: departDate,
        returnDate: returnDate,
        numLayovers: numLayovers,
    };

    return (
        <div className="App">
            <Page/>
        </div>

    );

}

export default Home;
